def add_color(x):
    try:
        x = float(x.rstrip('%'))  # Remove '%' and convert to float for comparison
        if x < 0:
            return 'color: red'  # Return CSS style string
        elif x > 0:
            return 'color: green'  # Return CSS style string
        else:
            return 'background-color: yellow'  # Return CSS style string
    except ValueError:
        return ''  # Handle cases where conversion to float fails


